import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Book } from './Book';

export default function BooksList ({reserveBook}){

    const [books, setBooks] = useState([]);

    useEffect(() => {
        axios.get("/api/books")
            .then(response => {
                setBooks(response.data);
            })
            .catch(error => {
                console.error("Klaida gaunant knygas:", error);
            });
    }, []);

    return (
        <div>
            <h1 className='title'>Knygų sąrašas</h1>
            <ul>
                {books.map(book => {
                    return <Book
                    {...book} 
                    key={book.id}
                    id={book.id}
                    title={book.title}
                    author={book.author}
                    year={book.year}
                    type={book.type}/>
                })}
            </ul>
        </div>
    );
};
