import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function ReservationSystem() {
    const { id } = useParams();
    const [book, setBook] = useState(null);
    const [isAudioBook, setAudioBook] = useState(false);
    const [isQuickPickUp, setQuickPickUp] = useState(false);
    const [reservationPeriod, setReservationPeriod] = useState(null);

    const navigate = useNavigate();

    const today = new Date().toISOString().split("T")[0];

    const handleAudioCheckBox = () => {
        setAudioBook((prev) => !prev);
        setQuickPickUp(false);
    };

    const handleQuickPickUpCheckBox = () => {
        setQuickPickUp((prev) => !prev);
    };

    const handleReservationPeriodChange = (event) => {
        setReservationPeriod(event.target.value);
    };

    useEffect(() => {
        axios.get(`/api/books/${id}`)
            .then(response => {
                setBook(response.data);
                setAudioBook(response.data.isAudioBook);
            })
            .catch(error => {
                console.error("Error retrieving book information:", error);
            });
    }, [id]);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const reservationData = {
            BookId: id,
            IsAudioBook: isAudioBook,
            IsQuickPickUp: isQuickPickUp,
            ReservationEnd: reservationPeriod
        };

        try {
            const response = await axios.post('/api/reservations/create', reservationData);
            console.log("Yay", response.data);
            navigate(-1);
        } catch (error) {
            console.error("Error", error);
        }
    };

    if (!book) {
        return <p>Loading...</p>;
    }

    return (
        <div>
            <h2>Reserve Book</h2>
            <p><strong>Title:</strong> {book.title}</p>
            <p><strong>Author:</strong> {book.author}</p>

            <form className="form" onSubmit={handleSubmit}>
                <label>
                    Audio Book
                    <input type="checkbox" checked={isAudioBook} onChange={handleAudioCheckBox}/>
                </label>

                <label>
                    Quick pick up
                    <input type="checkbox" checked={isQuickPickUp} onChange={handleQuickPickUpCheckBox} readOnly={isAudioBook}/>
                </label>

                <label>
                    Reservation period
                    <input type="date" value={reservationPeriod} onChange={handleReservationPeriodChange} min={today}/>
                </label>

                <button className="btn btn-submit" type="submit">Submit</button>
            </form>
        </div>
    );
}
