import React from 'react';

export function Book({id, title, author, year, type}){
    const navigate = useNavigate();

    const handleReservation = () =>{
        navigate(`/res/${id}`);
    }

    return(
        <li>
            <label>
                {title} - {author} ({year}) [{type}]
            </label>
            <button 
            onClick={handleReservation} 
            className="btn btn-reserve">Reserve</button>
        </li>
    );
}
