import React from 'react';

export default function OptionsForm({
    isAudioBook,
    isQuickPickUp,
    reservationPeriod,
    handleAudioCheckBox,
    handleQuickPickUpCheckBox,
    handleReservationPeriodChange,
    handleSubmit
}) {
    const today = new Date().toISOString().split("T")[0];

    return (
        <form className="form" onSubmit={handleSubmit}>
            <label className='txt'>
                Audio Book
                <input type="checkbox" checked={isAudioBook} onChange={handleAudioCheckBox} />
            </label>

            <label className='txt'>
                Quick pick up
                <input type="checkbox" checked={isQuickPickUp} onChange={handleQuickPickUpCheckBox} disabled={isAudioBook} />
            </label>

            <label className='txt'>
                Reservation period
                <input type="date" value={reservationPeriod} onChange={handleReservationPeriodChange} min={today} />
            </label>

            <button className="btn btn-submit" type="submit">Submit</button>
        </form>
    );
}
