import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ReservationSystem() {
    const { id } = useParams();
    const [book, setBook] = useState(null);
    const [isAudioBook, setAudioBook] = useState(false);

    const handleCheckBox = (event) =>{
        setAudioBook(event.target.checked);
    }

    useEffect(() => {
        axios.get(`/api/books/${id}`)
            .then(response => {
                setBook(response.data);
            })
            .catch(error => {
                console.error("Klaida gaunant knygos informaciją:", error);
            });
    }, [id]);

    if (!book) {
        return <p>Loading...</p>;
    }

    return (
        <div>
            <h2>Reserve Book</h2>
            <p><strong>Title:</strong> {book.title}</p>
            <p><strong>Author:</strong> {book.author}</p>

            <label>
                <input type="checkbox" checked={isAudioBook} onChange={handleCheckBox}/>
                Audio Book?
            </label>
            <p>{isAudioBook ? "This is an audiobook." : "This is not an audiobook."}</p>
            
        </div>
    );
}
