import React from 'react';

export function Book({id, title, author, year, type, reserveBook}){
    return(
        <li>
            <label>
                {title}
                {author}
                {year}
                {type}
            </label>
            <button 
            onClick={() => reserveBook(id)} 
            className="btn btn-reserve">Reserve</button>
        </li>
    );
}
