import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ReservationSystem() {
    const { id } = useParams();
    const [book, setBook] = useState(null);
    const [isAudioBook, setAudioBook] = useState(false);
    const [isQuickPickUp, setQuickPickUp] = useState(false);

    const handleAudioCheckBox = (event) =>{
        setAudioBook(event.target.value);
    }

    const handleQuickPickUpCheckBox = (event) =>{
        setAudioBook(event.target.value);
    }

    useEffect(() => {
        axios.get(`/api/books/${id}`)
            .then(response => {
                setBook(response.data);
            })
            .catch(error => {
                console.error("Klaida gaunant knygos informaciją:", error);
            });
    }, [id]);

    if (!book) {
        return <p>Loading...</p>;
    }

    return (
        <>
        <div>
            <h2>Reserve Book</h2>
            <p><strong>Title:</strong> {book.title}</p>
            <p><strong>Author:</strong> {book.author}</p>

            <label>
            Audio Book?
                <input type="checkbox" checked={isAudioBook} onChange={handleAudioCheckBox}/>
            </label>

            <label>
            Quick pick up?
                <input type="checkbox" checked={isAudioBook} onChange={handleQuickPickUpCheckBox}/>
            </label>

        </div>
        </>  
    );
}
