import React from 'react';
import './App.css';
import {
    BrowserRouter as Router,
    Routes,
    Route,
} from "react-router-dom";
import BooksList from './BooksList';
import { NavBar } from './Navbar';
import Modal from './Modal';

export default function App() {
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedBookId, setSelectedBookId] = useState(null);

    function reserveBook(id){
        setSelectedBookId(id);
        setModalOpen(true);
    }

    function closeModal() {
        setModalOpen(false);
    }

    return (
        <>
        <div className="App">
            <header className="App-header">
                <h1 className='main-title'>Welcome to the library!</h1>
            </header>
        </div>
        <Router>
            <NavBar />
            <Routes>
                <Route path="/other" element={<BooksList reserveBook={reserveBook}/>} />
            </Routes>
        </Router>
        <Modal isOpen={isModalOpen} close={closeModal}>
            <h2>Book Details</h2>
            {/* Here you can add more content about the book using selectedBookId */}
            <p>Book ID: {selectedBookId}</p>
        </Modal>
        </>
    );
}
