import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BooksList = () => {
    // Knygų saugojimui state'e
    const [books, setBooks] = useState([]);

    // Užklausos API per Axios
    useEffect(() => {
        axios.get("/api/books")
            .then(response => {
                setBooks(response.data); // Nustatome gautas knygas į state
            })
            .catch(error => {
                console.error("Klaida gaunant knygas:", error);
            });
    }, []); // [] reiškia, kad useEffect bus vykdomas tik vieną kartą, kai komponentas užkraunamas

    return (
        <div>
            <h1>Knygų sąrašas</h1>
            <ul>
                {books.map(book => (
                    <li key={book.id}>{book.title} ({book.year})</li>
                ))}
            </ul>
        </div>
    );
};

export default BooksList;
