import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Book } from './Book';

const BooksList = () => {
    // Knygų saugojimui state'e
    const [books, setBooks] = useState([]);

    // Užklausos API per Axios
    useEffect(() => {
        axios.get("/api/books")
            .then(response => {
                setBooks(response.data); // Nustatome gautas knygas į state
            })
            .catch(error => {
                console.error("Klaida gaunant knygas:", error);
            });
    }, []);

    return (
        <div>
            <h1>Knygų sąrašas</h1>
            <ul>
                {books.map(book => {
                    return <Book
                    {...book} 
                    key={book.id}
                    id={book.id}
                    title={book.title}
                    author={book.author}
                    year={book.year}
                    type={book.type}/>
                })}
            </ul>
        </div>
    );
};

export default BooksList;
