import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Book } from './Book';

export default function BooksList() {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);
    const [titleFilter, setTitleFilter] = useState("");
    const [yearFilter, setYearFilter] = useState("");
    const [typeFilter, setTypeFilter] = useState("all");

    useEffect(() => {
        axios.get("/api/books")
            .then(response => {
                setBooks(response.data);
                setFilteredBooks(response.data);
            })
            .catch(error => {
                console.error("Klaida gaunant knygas:", error);
            });
    }, []);

    const handleFilter = () => {
        let filtered = books;

        if (titleFilter) {
            filtered = filtered.filter(book =>
                book.title.toLowerCase().includes(titleFilter.toLowerCase())
            );
        }

        if (yearFilter) {
            filtered = filtered.filter(book => book.year.startsWith(yearFilter));
        }

        if (typeFilter !== "all") {
            filtered = filtered.filter(book =>
                typeFilter === "audiobook" ? book.isAudioBook : !book.isAudioBook
            );
        }

        setFilteredBooks(filtered);
    };

    const handleClearFilters = () => {
        setTitleFilter("");
        setYearFilter("");
        setTypeFilter("all");
        setFilteredBooks(books);
    };

    return (
        <>
            <form>
                <input 
                    type="text" 
                    placeholder="Filter by title" 
                    value={titleFilter}
                    onChange={(e) => setTitleFilter(e.target.value)} 
                />
                <input 
                    type="number" 
                    placeholder="Filter by year" 
                    value={yearFilter}
                    onChange={(e) => setYearFilter(e.target.value)} 
                />
                <select value={typeFilter} onChange={(e) => setTypeFilter(e.target.value)}>
                    <option value="all">All</option>
                    <option value="audiobook">Audio Book</option>
                    <option value="book">Regular Book</option>
                </select>

                <button type="button" onClick={handleFilter}>Filter</button>
                <button type="button" onClick={handleClearFilters}>Clear Filters</button>
            </form>

            <div className="items-container">
                <h1 className="title">Knygų sąrašas</h1>
                <ul>
                    {filteredBooks.map(book => (
                        <Book
                            {...book} 
                            key={book.id}
                            id={book.id}
                            title={book.title}
                            author={book.author}
                            year={book.year}
                            image={book.image}
                        />
                    ))}
                </ul>
            </div>
        </>
    );
}
